	function prompts(text, placeholder) {
	  return new Promise((resolve, reject) => {
	    const modalOverlay = document.createElement('div');
	    modalOverlay.id = 'prompt';
	    modalOverlay.className = 'modal-overlay';
	    modalOverlay.style.zIndex = "999";

	    const modal = document.createElement('div');
	    modal.className = 'modal';
	    modal.style.display = "flex";
	    modal.style.flexDirection = "column";

	    const title = document.createElement('h2');
	    title.id = 'title';
	    title.innerHTML = `${text} <span id="x" class="close" title="Cancel">&times;</span>`;

	    const input = document.createElement('input');
	    input.type = 'text';
	    input.id = 'p_text';
	    input.autocomplete = 'off';

	    if (placeholder) {
	      input.value = placeholder;
	    }

	    const confirm = document.createElement('button');
	    confirm.id = 'conf';
	    confirm.title = 'Set';
	    confirm.style.cssText = `
	        padding: 10px 20px;
	        margin: 5px;
	    `;
	    confirm.innerText = 'Confirm';

	    confirm.addEventListener('click', () => {
    	  if(!input.value) return;
	      resolve(input.value);
	      modalOverlay.remove();
	    });

	    modal.appendChild(title);
	    modal.appendChild(input);
	    modal.appendChild(confirm);
	    modalOverlay.appendChild(modal);
	    document.body.appendChild(modalOverlay);

	    modalOverlay.style.display = 'flex';
	    input.select();

	    const closeModal = () => {
	      reject(new Error('Modal closed.'));
	      modalOverlay.remove();
	    };

	    document.getElementById('x').addEventListener('click', closeModal);
	    
	    input.addEventListener('keyup', (e) => {
	      if (e.key === 'Enter') {
	      	if(!input.value) return;
	        resolve(input.value);
	        modalOverlay.remove();
	      }
	    });
	  });
	}

